CREATE TABLE IF NOT EXISTS animal_types(id INTEGER PRIMARY KEY, code TEXT, animal_type TEXT, short_name TEXT, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));
CREATE TABLE IF NOT EXISTS animals(id INTEGER PRIMARY KEY, code TEXT NOT NULL, animal_type_code TEXT NOT NULL, animal_name TEXT NOT NULL, animal_number TEXT NOT NULL, DOB TIMESTAMP, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));
CREATE TABLE IF NOT EXISTS products(id INTEGER PRIMARY KEY, code TEXT NOT NULL, animal_code TEXT NOT NULL, product_name TEXT, short_name TEXT, measurement_unit TEXT, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));
CREATE TABLE IF NOT EXISTS product_pricing(id INTEGER PRIMARY KEY, code TEXT NOT NULL, product_code TEXT NOT NULL, price TEXT NOT NULL, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));
CREATE TABLE IF NOT EXISTS foods(id INTEGER PRIMARY KEY, code TEXT NOT NULL, food_name TEXT NOT NULL, measurement_unit TEXT NOT NULL, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));
CREATE TABLE IF NOT EXISTS food_pricing(id INTEGER PRIMARY KEY, code TEXT NOT NULL, food_code TEXT NOT NULL, price TEXT NOT NULL, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));
CREATE TABLE IF NOT EXISTS consumption(id INTEGER PRIMARY KEY, code TEXT NOT NULL, animal_code TEXT NOT NULL, food_code TEXT NOT NULL, consumed_unit TEXT NOT NULL, consumption_amount DOUBLE NOT NULL, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));
CREATE TABLE IF NOT EXISTS production(id INTEGER PRIMARY KEY, code TEXT NOT NULL, animal_code TEXT NOT NULL, product_code TEXT NOT NULL, production_unit TEXT NOT NULL, production_amount DOUBLE NOT NULL, status TINYINT(5) DEFAULT '1' , created_at DATETIME, updated_at DATETIME, UNIQUE (code));